mapper.select:
select id,name,uri,type,puri,deleted from permission


        and id = #{id}


        and type = #{type}


        and puri = #{puri}


        and enabled = #{enabled}


        and deleted = #{deleted}

mapper.insert:
insert into permission (name, uri, type, puri)
values (
#{name,jdbcType=VARCHAR},#{uri,jdbcType=VARCHAR},
#{type,jdbcType=VARCHAR},#{puri,jdbcType=VARCHAR}
)
